import {useEffect, useState} from "react";

function PostList() {
    const [post, setPosts] = useState([]);
    useEffect(
        (
            async () => {
                const res = await fetch(`https://jsonplaceholder.typicode.com/posts`);
                const data = await res.json();

                setPosts(post);
            }
        )()
    );

    return (
        <div>Posts Page</div>
    )// Render data...
}

