import {useEffect} from "react";

export default function Post({id}) {
    const [post, setPost] = useState(null);
    useEffect(() => {
        const storage = window.localStorage;
        const post = storage.getItem(`post-${id}`);

        setPost(post);
    }, []);

    return (
        <div>
            User: {post.userId}
            Post Title: {post.title}
            Post Body:
            {post.body}
        </div>
    )
}
