import React, {useEffect, useState} from "react";
import Login from "../components/login";
import {pageIds} from "../helpers/constants";
import PostList from "../components/posts";
import Post from "../components/post";
import * as Base64 from "../.next/static/development/pages";

export default function Index() {
    const [isLoginPage, setPage] = useState(false);
    const [postId, setPostId] = useState("");
    useEffect(() => {
        const storage = window.localStorage;
        const page = storage.getItem("page");
        const postId = storage.getItem("postId");
        const token = storage.getItem("token");

        if (!token) {
            setPage(true)
        }

        if (postId) {
            setPostId(postId)
        }

        if (!page) {
            const postId = storage.getItem("postId");
            storage.setItem("page", pageIds.loginPage);
            setPage(true);
            if (postId !== "") {
                setPostId()
            }
        }
    }, []);

    const handlePostOpen = (id) => {
        setPostId(id);
        window.localStorage.setItem("postId", id);
    };

    const handlePostListOpen = () => {
        setPostId("");
        window.localStorage.setItem("postId", "");
        window.localStorage.setItem("page", "posts");
    };

    const handleSignIn = (password) => {
        const token = Base64.encode(password);
        window.localStorage.setItem("token", token);
        setPage(false);
    };

    const drawPage = () => {
        if (isLoginPage) {
            return (
                <Login handleSignIn={handleSignIn}/>
            )
        } else if (postId === "") {
            return (
                <PostList handlePostOpen={handlePostOpen} />
                )
        } else {
            debugger
            return (
                <Post handlePostListOpen={handlePostListOpen} id={postId}/>
            )
        }
    };

    return (
        <div>
            {
                drawPage()
            }
        </div>
    );
}
