import React, {useEffect, useState} from "react";
import Login from "../components/login";
import {pageIds} from "../helpers/constants";
import PostList from "../components/posts";
import Post from "../components/post";

export default function Index() {
    const [isLoginPage, setPage] = useState(false);
    const [postId, setPostId] = useState("");
    useEffect(() => {
        const storage = window.localStorage;
        let userLogin = storage.getItem("login");
        let userPassword = storage.getItem("password");
        let page = storage.getItem("page");
        if (!userLogin || !userPassword) {
            storage.setItem("login", "admin");
            storage.setItem("password", "admin");
        }

        if (!page) {
            const postId = storage.getItem("postId");
            storage.setItem("page", pageIds.loginPage);
            setPage(true);
            if (postId !== "") {
                setPostId()
            }
        }
    }, []);

    const handlePostOpen = (id) => {
        setPostId(id);
        window.localStorage.setItem("postId", id);
    };

    const drawPage = () => {
        if (isLoginPage) {
            return (
                <Login/>
            )
        } else if (postId === "") {
            return (
                <PostList handlePostOpen={handlePostOpen} />
                )
        } else {
            return (
                <Post id={postId}/>
            )
        }
    };

    return (
        <div>
            {
                drawPage()
            }
        </div>
    );
}
